package be.bitbox.alledaags.harvesters.onroerenderfgoed;

import org.junit.jupiter.api.Test;
import org.springframework.web.client.RestTemplate;

import java.io.IOException;
import java.util.Objects;

import static org.assertj.core.api.Assertions.assertThat;

class BeeldBankTest {

    @Test
    void testHTMLWithImage() throws IOException {
        var html = new String(Objects.requireNonNull(getClass().getClassLoader().getResourceAsStream("beeldbank.html")).readAllBytes());
        var imageSrc = BeeldBank.getImageSrc(new RestTemplate(), html);

        assertThat(imageSrc).isEqualTo("data:image/jpeg;base64,");
    }

    @Test
    void testNoImage() throws IOException {
        var html = new String(Objects.requireNonNull(getClass().getClassLoader().getResourceAsStream("no_image_beeldbank.html")).readAllBytes());
        var imageSrc = BeeldBank.getImageSrc(new RestTemplate(), html);

        assertThat(imageSrc).isEqualTo("https://inventaris.onroerenderfgoed.be/static/img/gn_afbeelding.gif");
    }
}